<script>
export default {
  name: "HeaderButton",
  data() {
    return {
      isMounted: false,
    };
  },
  mounted() {
    const observer = new IntersectionObserver(
      (entry) => {
        if (entry[0].isIntersecting) {
          this.isMounted = true;
          this.$refs.firstButton.style.transform = "translateX(0px)";
          this.$refs.secondButton.style.transform = "translateX(0px)";
          observer.unobserve(this.$refs.buttonsContainer);
        }
      },
      {
        threshold: 0.7,
      }
    );
    observer.observe(this.$refs.buttonsContainer);
  },
};
</script>

<style lang="scss">
.header {
  &__first-button {
    transition: all 1s cubic-bezier(0.17, 0.67, 0.27, 1.27);
    transform: translateX(100px);
    transition-delay: 500ms;
  }
  &__second-button {
    transition: all 1s cubic-bezier(0.17, 0.67, 0.27, 1.27);
    transform: translateX(-100px);
    transition-delay: 500ms;
  }
  &__arrow {
    transition: all 1s;
    transition-delay: 1.5s;
  }
}
</style>